<?php/*		  _______________//		 | 				 |//		 |	M.H.GOLKAR	 |//		 |	 FlatFire	 |//		 |_______________|//::: html special chars replacement:::*/// fanction to replace html special charsfunction hsc($in){if(	gettype(strpos($in,"<"))=="integer"	|| gettype(strpos($in,">"))=="integer"	|| gettype(strpos($in,"\""))=="integer"	|| gettype(strpos($in,"&#38;"))=="integer"	|| gettype(strpos($in,"&#39;"))=="integer"	|| gettype(strpos($in,"&#34;"))=="integer"		) return htmlentities($in,ENT_QUOTES,"UTF-8");else return $in;}?>