mj"]) && isset($_POST["ir"])){		cheatcon($_POST["imj"]);		$successfi = @delete_row($_POST["iht"],$_POST["ir"]);		if($successfi!==false)			$raportaj ="Row number \"".hsc($_POST["ir"]+1)."\" Deleted";		else{			$erica = error_get_last();			if($erica!=null) $raportaj = $erica["message"];			else $raportaj = "Internal Error... job failed";		}	}	// CONTENTif(!isset($_GET["d"])) echo"<span class=\"centrino\"><br/>Select database or element to Edit... <br> to toggle databases duble click on database name...</span>\n";if(isset($_GET["s"]) && isset($_GET["d"]) && in_array($_GET["s"],$tanar)){	if (in_array($_GET["d"],$tree["list"])) cheatcon ($_GET["d"]); // Cheat Connect	if(table_exists($_GET["s"])) {		$tablesres = array();		$parsa = parse_src($_GET["s"]);		echo "		<input type=\"textarea\" style=\"Display:none\" name=\"imj\" value=\"".$_GET["d"]."\"/>			<table class=\"tenplsu\" border=\"1\">			<caption>Database: [".hsc($_GET["d"])."]  Table:[".hsc($_GET["s"])."]</caption>			<tr class=\"trpink\">";								   echo"<th></th>";			foreach($parsa as $po){echo "<th>".hsc($po)."</th>";}			echo"			</tr>";		for($co=0;$co<=get_row_count($_GET["s"]);$co++){			echo"			<tr".($co%2!==0?" class=\"rawrow\"":"").">";				echo "<td><input type=\"radio\" value=\"".$co."\" name=\"ir\"/></td>";				$inro = @get_row($_GET["s"],$co);				if(count($inro)>1){					foreach($inro as $row){echo "<td>".hsc($row)."</td>";}				}				else echo"<td>EMPTY TABLE</td>";			echo"</tr>";		}	echo "			<input type=\"textarea\" style=\"Display:none\" name=\"iht\" value=\"".$_GET["s"]."\"/>			<input type=\"submit\" name=\"do\" value=\"Remove Row\"/>			<input type=\"submit\" name=\"do\" value=\"Delete Table\"/>		";	}	if(free_existance($_GET["s"])){		$fara = @get_free($_GET["s"]);		if(count($fara)>0){		echo "			<input type=\"textarea\" style=\"Display:none\" name=\"imj\" value=\"".$_GET["d"]."\"/>			<table class=\"tenplsu\" border=\"1\">			<caption>Database: [".hsc($_GET["d"])."]  Element: [".hsc($_GET["s"])."]</caption>			<tr style=\"background-color:pink\">			<th> </th>	<th>Fields</th>	<th>Values</th>			</tr>";		reset($fara);			for($iud=0;$iud<count($fara);$iud++){ 				echo"<tr ".($iud==0?"class=\"idrow\"":"").">".						($iud!=0?"<td><input type=\"radio\" value=\"".key($fara)."\" name=\"ik\" /></td>":"<td></td>")						."<td>".hsc(key($fara))."</td>						<td>".hsc(current($fara))."</td>					</tr>";				next($fara);			}		} else echo "<span> Empty Element </span>";	echo "			<input type=\"textarea\" style=\"Display:none\" name=\"dohl\"/>			<input type=\"textarea\" style=\"Display:none\" name=\"ih\" value=\"".$_GET["s"]."\"/>			<input type=\"submit\" name=\"do\" value=\"Remove Selected\"/>			<input type=\"submit\" name=\"do\" value=\"Delete Element\"/>		";	}}?><input style="display:none;" name="l" value="Edit" />