\">";			foreach($tree["db"][$db]["tables"] as $feb){echo "\n\t<li onmouseup=\"FFDB_Select('ffdb_dbs_".hsc($db)."');\" onclick=\"FFDB_Select('ff_".hsc($db)."_el_".hsc($feb)."');\" ><input type=\"radio\" value=\"".$feb."\"  id=\"ff_".hsc($db)."_el_".hsc($feb)."\" name=\"s\" onclick=\"FFDB_Select('ffdb_dbs_".hsc($db)."');\" />".hsc($feb)."</li>" ;			array_push($tanar,$feb);			} 			echo "</ul>";	}	echo "			</div>		</ul>\n";}echo "</ul>\n";if(isset($_GET["d"]) && isset($_GET["s"])){echo"<script>window.onload = function(){FFDB_Select(\"ffdb_dbs_".hsc($_GET["d"])."\");FFDB_Select(\"ff_".hsc($_GET["d"])."_el_".hsc($_GET["s"])."\");}</script>";}?></fieldset><br/></div>