<?phpnamespace FFDB;/*		  _______________//		 | 				 |//		 |	M.H.GOLKAR	 |//		 |	 FlatFire	 |//		 |	01.00[2012]	 |//		 |_______________|//::: Control panel Root Data editor:::*/if (isset($_POST["ff_root_Change"])){if (isset($_SESSION["ROOT"]) && isset($_SESSION["PASS"])){	if(		isset($_POST["cur_Root_us"]) && $_POST["cur_Root_us"]===$_SESSION["ROOT"]		&& isset($_POST["cur_Root_pa"]) && $_POST["cur_Root_pa"]===$_SESSION["PASS"]	){		if(			isset($_POST["new_Root_us"]) && isset($_POST["new_Root_pa"])			&& $_POST["new_Root_us"] !=null && $_POST["new_Root_pa"] !=null			&& $_POST["new_Root_us"] !="null" && $_POST["new_Root_pa"] !="null"		){				$opicfa = @fopen(_FFDBDIR_."Root.rut","r+"); 				if($opicfa){				set_file_buffer($opicfa,0);				ftruncate($opicfa,0);				rewind($opicfa);				fwrite($opicfa,$_POST["new_Root_us"]."[=]".$_POST["new_Root_pa"]);				fclose($opicfa);				$_SESSION["ROOT"] = $_POST["new_Root_us"];				$_SESSION["PASS"] = $_POST["new_Root_pa"];				$reportaj = "Done...";				} else $reportaj = "internal error, unable to write in root handler...";		}		else $reportaj = "Error! New username or password is null";	}	else $reportaj = "Error! Current username or password is wrong";}else header("Location: ".$_SERVER["PHP_SELF"]."?p=logout");}echo "<h1>ROOT SETTINGS</h1>";include_once(_FFDIR_."Panel/Includes/Settings/Toolbar.php");?><div id="ff_Rootary"><h1>Change Root</h1><br/><form action="<?php echo $_SERVER["PHP_SELF"]."?".$_SERVER["QUERY_STRING"]?>" method="POST"><b>Current Root</b><br/><span>user </span><input type="textarea" name="cur_Root_us" /><br/><span>pass</span><input type="password" name="cur_Root_pa" /><br/><b>New Root</b><br/><span>user </span><input type="textarea" name="new_Root_us" /><br/><span>pass</span><input type="password" name="new_Root_pa" /><br/><br/><input type="submit" class="creatbot" name="ff_root_Change" value="Change" /></form></div><?phpif(@$reportaj!=null){echo"<div id=\"ff_new_report\" >	<p>".$reportaj."</p></div><br/>";}?>